const question = $input.first().json.question;

// 简单的诗词知识库
const knowledge = {
  '李白': '李白是唐代伟大的浪漫主义诗人，被后人誉为"诗仙"。代表作品有《静夜思》、《将进酒》、《蜀道难》等。',
  '杜甫': '杜甫是唐代伟大的现实主义诗人，被尊为"诗圣"。代表作品有《春望》、《登高》、《茅屋为秋风所破歌》等。',
  '苏轼': '苏轼是北宋文学家、书画家，豪放派词人代表。代表作品有《水调歌头》、《念奴娇·赤壁怀古》等。',
  '唐诗': '唐诗是中国古典诗歌的黄金时代，分为初唐、盛唐、中唐、晚唐四个时期。',
  '宋词': '宋词是宋代文学的代表，分为婉约派和豪放派，句式灵活多变。',
  '推荐': '我推荐李白的《静夜思》：床前明月光，疑是地上霜。举头望明月，低头思故乡。',
  '解释': '请提供具体的诗句，我会为您详细解释其含义和背景。'
};

let answer = '关于诗词的问题，我建议您查阅《唐诗三百首》或《宋词三百首》等经典选集。';

for (const [key, value] of Object.entries(knowledge)) {
  if (question.includes(key)) {
    answer = value;
    break;
  }
}

return [{ json: { answer, success: true } }];
